mapper.select:
SELECT cproEvaluation.pkId AS evaluationId, cproEvaluation.fkSystemId, cproEvaluation.examName AS examName, cproEvaluation.examTime AS examTime, cproEvaluation.examYear AS examYear, cproEvaluation.examOrg AS examOrg, cproAttach.pkId AS examReport, cproAttach.attachName AS examReportName, cproEvaluation.fkExamStatus AS fkExamStatus, cproEvaluation.fkExamResult AS fkExamResult, cproEvaluation.fkRectificationReu AS fkRectificationReu, rectificationDate AS rectificationDate, cproAttachRectification.pkId AS rectificationReport, cproAttachRectification.attachName AS rectificationReportName FROM t_cpro_evaluation AS cproEvaluation LEFT JOIN t_cpro_attach AS cproAttach ON cproAttach.fkSyssonId = cproEvaluation.pkId AND cproAttach.fkSystemId = cproEvaluation.fkSystemId AND cproAttach.fkAttachType = 'evaluationPresentation' LEFT JOIN t_cpro_attach AS cproAttachRectification ON cproAttachRectification.fkSyssonId = cproEvaluation.pkId AND cproAttachRectification.fkSystemId = cproEvaluation.fkSystemId AND cproAttachRectification.fkAttachType = 'rectificationReport'
AND cproEvaluation.pkId = #{evaluationId}
LIMIT 0,1

SELECT cproEvaluation.pkId AS evaluationId, cproEvaluation.fkSystemId, cproEvaluation.examName AS examName, cproEvaluation.examTime AS examTime, cproEvaluation.examYear AS examYear, cproEvaluation.examOrg AS examOrg, cproAttach.pkId AS examReport, cproAttach.attachName AS examReportName, cproEvaluation.fkExamStatus AS fkExamStatus, cproEvaluation.fkExamResult AS fkExamResult, cproEvaluation.fkRectificationReu AS fkRectificationReu, rectificationDate AS rectificationDate, cproAttachRectification.pkId AS rectificationReport, cproAttachRectification.attachName AS rectificationReportName FROM t_cpro_evaluation AS cproEvaluation LEFT JOIN t_cpro_attach AS cproAttach ON cproAttach.fkSyssonId = cproEvaluation.pkId AND cproAttach.fkSystemId = cproEvaluation.fkSystemId AND cproAttach.fkAttachType = 'evaluationPresentation' LEFT JOIN t_cpro_attach AS cproAttachRectification ON cproAttachRectification.fkSyssonId = cproEvaluation.pkId AND cproAttachRectification.fkSystemId = cproEvaluation.fkSystemId AND cproAttachRectification.fkAttachType = 'rectificationReport'
AND cproEvaluation.pkId = #{evaluationId}
LIMIT 0,1
