mapper.select:
SELECT codeExaminStatus.codeName AS examinStatusName, systemKeyProducts.productsNumber, codeNationalIsProducts.codeName AS nationalIsProductsName, systemKeyProducts.nUseProbability, systemKeyProducts.fkExaminStatus, systemKeyProducts.fkNationalIsProducts, systemKeyProducts.otherName FROM t_cpro_system_key_products AS systemKeyProducts LEFT JOIN t_cpro_system AS system ON systemKeyProducts.fkSystemId = system.pkId LEFT JOIN t_cpro_system_code AS codeExaminStatus ON systemKeyProducts.fkExaminStatus = codeExaminStatus.systemCode AND codeExaminStatus.fkCodeType = 12 LEFT JOIN t_cpro_system_code AS codeNationalIsProducts ON systemKeyProducts.fkNationalIsProducts = codeNationalIsProducts.systemCode AND codeNationalIsProducts.fkCodeType = 9
AND systemKeyProducts.fkSystemId = #{systemId,jdbcType=VARCHAR}
ORDER BY systemKeyProducts.fkExaminStatus

SELECT codeProductsType.codeName AS productsTypeName, codeUse.codeName AS useName, codeResponsibleType.codeName AS responsibleTypeName, systemUseServices.otherName, systemUseServices.fkProductsType, systemUseServices.fkResponsibleType, systemUseServices.serviceIsUse FROM t_cpro_system_use_services AS systemUseServices LEFT JOIN t_cpro_system AS system ON systemUseServices.fkSystemId = system.pkId LEFT JOIN t_cpro_system_code AS codeProductsType ON systemUseServices.fkProductsType = codeProductsType.systemCode AND codeProductsType.fkCodeType = 14 LEFT JOIN t_cpro_system_code AS codeUse ON systemUseServices.serviceIsUse = codeUse.systemCode AND codeUse.fkCodeType = 10 LEFT JOIN t_cpro_system_code AS codeResponsibleType ON systemUseServices.fkResponsibleType = codeResponsibleType.systemCode AND codeResponsibleType.fkCodeType = 15
AND systemUseServices.fkSystemId = #{systemId,jdbcType=VARCHAR}
ORDER BY systemUseServices.fkProductsType

SELECT system.pkId AS systemId, system.systemName, system.sysBusSituationType, system.sysBusDescription, system.whenInvestmentUse FROM t_cpro_system AS system LEFT JOIN t_cpro_company AS company ON system.fkCompanyCode = company.companyCode
AND company.companyCode IN
#{item}
AND company.fkPlateType IN
#{item}
AND system.systemName LIKE #{systemName,jdbcType=VARCHAR}

mapper.update:
UPDATE t_cpro_system 
SET


    gradingStatus = #{gradingStatus,jdbcType=INTEGER}




    examineStatus = #{examineStatus,jdbcType=INTEGER}




    companyName = #{companyName,jdbcType=VARCHAR}




    recordStatus = #{recordStatus,jdbcType=INTEGER}




    evaluationStatus = #{evaluationStatus,jdbcType=INTEGER}




    examinationStatus = #{examinationStatus,jdbcType=INTEGER}


WHERE pkId = #{systemId}

mapper.select:
SELECT codeName level,COUNT(b.fkSpRanklevel) num FROM
(SELECT fkCodeType,codeName,systemCode FROM t_cpro_system_code WHERE
fkCodeType = '11' AND deleteStatus = '1' AND systemFatherCode = '3') a
LEFT JOIN
(SELECT scor.pkId,scor.fkSpRanklevel,scor.deleteStatus FROM t_cpro_score scor INNER JOIN
t_cpro_system sys ON sys.pkId = scor.fkSystemId  AND gradingStatus = '3'


    AND sys.deleteStatus = #{deleteStatus,jdbcType=INTEGER}




      AND sys.updateTime >= #{updateTimeBegin,jdbcType=DATE}




      AND sys.updateTime <= #{updateTimeEnd,jdbcType=DATE}




    AND sys.evaluationStatus = #{evaluationStatus,jdbcType=INTEGER}




    AND sys.examinationStatus = #{examinationStatus,jdbcType=INTEGER}




    AND sys.examineStatus = #{examineStatus,jdbcType=INTEGER}




    AND sys.recordStatus = #{recordStatus,jdbcType=INTEGER}




    AND sys.fkInfoSysTypeCon = #{fkInfoSysTypeCon,jdbcType=INTEGER}



) b
ON a.systemCode = b.fkSpRanklevel GROUP BY codeName,b.fkSpRanklevel ORDER BY level ASC

